import { useEffect } from "react";
import {
  BrowserRouer,
  Routes,
  Route,
  useLocation
} from 'react-router-dom';

const ScrollToTop = () => {
  const { pathname } = useLocation();

  useEffect(() => {
    window.scrollTo(0,0);
  }, [pathname]);
}

function App() {
  return (
    <div className="app">
    </div>
  );
}

export default App;
