import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { IconButton, Box, Typography, Button, Tabs, Tab } from "@mui/material";
import FavoriteBorderOutlinedIcon from "@mui/icons-material/FavoriteBorderOutlined";
import { useParams } from "react-router-dom";
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import { shades } from "../../theme";
import { addToCart } from "../../state";
import Item from "../../components/Item";

const ItemDetails = () => {
    const dispatch = useDispatch();
    const { itemId } = useParams();
    const [value, setValue] = useState("description");
    const [count, setCount] = useState(1);
    const [item, setItem] = useState(null);
    const [items, setItems] = useState([]);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    }

    async function getItem() {
        const item = await fetch(
            `http://localhost:1337/api/items/${itemId}?populate=image`,
            { method: "GET" }
        );
        const itemJson = await item.json();
        setItem(itemJson.data);
    }

    async function getItems() {
        console.log("ðŸš€ ~ getItems:", getItems)
        const items = await fetch(
            "http://localhost:1337/api/items?populate=image",
            { method: "GET" }
        );
        const itemsJson = await items.json();
        setItems(itemsJson.data);
    }

    useEffect(() => {
        getItem();
        getItems();
    }, [itemId]);


    return (
        <Box width="80%" m="80px auto">
            <Box display="flex" flexWrap="wrap" columnGap="40px">
                {/* Images */}
                <Box flex="1 1 40%" mb="40px">
                    <img alt={item?.name}
                        height="100%" width="100%"
                        src={`http://localhost:1337/${item?.attributes?.image?.data?.attributes?.formats?.medium?.url}`} 
                        style={{ objectFit: "contain" }}
                    />
                </Box>

                {/* Actions */}
                <Box flex="1 1 50%" mb="40px">
                    <Box display="flex" justifyContent="space-between">
                        <Box>Home/Item</Box>
                        <Box>Prev Next</Box>
                    </Box>

                    <Box m="65px 0 25px 0">
                        <Typography variant="h3">{item?.attributes?.name}</Typography>
                        <Typography>${item?.attributes?.price}</Typography>
                        <Typography sx={{ mt:"20px" }}>
                            {item?.attributes?.longDescription}
                        </Typography>
                    </Box>

                    {/* Item count and add to cart buttons*/}
                    <Box display="flex" alignItems="Center" minHeight="50px">
                        {/* Item count button */}
                        <Box display="flex" alignItems="Center" mr="20px"
                            border={`1.5px solid ${shades.neutral[300]}`}
                            p="2px 5px"
                        >
                            <IconButton onClick={() => setCount(Math.max(count - 1, 1)) }>
                                <RemoveIcon />
                            </IconButton>

                            <Typography sx={{ p: "0 5px" }}>{count}</Typography>

                            <IconButton onClick={() => setCount(count + 1)}>
                                <AddIcon />
                            </IconButton>
                        </Box>

                        {/* Add to cart button */}
                        <Button 
                            sx={{
                                color: "white",
                                backgroundColor: "#222222",
                                borderRadius: 0,
                                minWidth: "150px",
                                padding: "10px 40px"
                            }}
                            onClick={() => dispatch(addToCart({ item: { ...item, count } }))}
                        >
                            ADD TO CART
                        </Button>
                    </Box>

                    {/* Add to wishlist, button has no action */}
                    <Box>
                        <Box m="20px 0 10px 0" display="flex">
                            <FavoriteBorderOutlinedIcon />
                            <Typography sx={{ ml: "5px" }}>Add to wishlist</Typography>
                        </Box>
                        <Typography>Categories: {item?.attributes?.category}</Typography>
                    </Box>
                </Box>
            </Box>

            {/* Information for description and reviews */}
            <Box m="20px 0">
                <Tabs value={value} onChange={handleChange}>
                    <Tab label="DESCRIPTION" value="description"></Tab>
                    <Tab label="REVIEWS" value="reviews"></Tab>
                </Tabs>
            </Box>
            <Box display="flex" flexWrap="wrap" gap="15px">
                {value === "description" && (
                    <div>{item?.attributes?.longDescription}</div>
                )}
                {value === "reviews" && <div>No Reviews Yet</div>}
            </Box>

            {/* Related Products */}
            <Box mt="50px" width="100%">
                <Typography variant="h3" fontWeight="bold">Related Products</Typography>
                <Box mt="20px" display="flex" flexWrap="wrap"
                    columnGap="1.33%" justifyContent="space-between"
                >
                    {items.slice(0, 4).map((item, i) => (
                        <Item item={item} key={`${item.name}-${i}`} />
                    ))}
                </Box>
            </Box>
        </Box>
    )
}

export default ItemDetails;
