import { useDispatch, useSelector } from 'react-redux';
import { Badge, Box, IconButton } from "@mui/material";
import { PersonOutlined, 
    ShoppingBagOutlined,
    MenuOutlined,
    SearchOutlined
} from "@mui/icons-material";
import { useNavigate } from "react-router-dom";
import { shades } from "../../theme";
import { setIsCartOpen } from '../../state';

const Navbar = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const cart = useSelector((state) => state.cart.cart);

    return (
        <Box display="flex" alignItems="center" width="100%" height="60px"
            backgroundColor="rgba(255,255,255,0.9)" color="black" position="fixed"
            top="0" left="0" zIndex="1">
            <Box width="80%" margin="auto" display="flex"
                justifyContent="space-between" alignItems="center"
            >
                <Box onClick={() => navigate("/")}
                    sx={{ '&:hover': { cursor: "pointer" } }}
                    color={shades.secondary[400]} fontWeight="bold"
                    fontSize="32px"
                >
                    NewClo
                </Box>
                <Box display="flex" justify-content="space-between" columnGap="20px" zIndex="2">
                    <IconButton sx={{ color: "black" }}>
                        <SearchOutlined />
                    </IconButton>

                    <IconButton sx={{ color: "black" }}>
                        <PersonOutlined />
                    </IconButton>

                    <Badge badgeContent={cart.length} color="secondary" invisible={cart.length === 0}
                        sx={{
                            "& .MuiBadge-badge": {
                                right: 5,
                                top: 5,
                                padding: "0 4px",
                                height: "14px",
                                minWidth: "13px",
                            }
                    }}>
                        <IconButton sx={{ color: "black" }} 
                            onClick={() => dispatch(setIsCartOpen({}))}
                        >
                            <ShoppingBagOutlined />
                        </IconButton>
                    </Badge>

                    <IconButton sx={{ color: "black" }}>
                        <MenuOutlined />
                    </IconButton>
                </Box>
            </Box>
        </Box>
    )
}

export default Navbar;
