import { Box, InputBase, Divider, Typography, IconButton } from '@mui/material';
import MarkEmailReadOutlinedIcon from '@mui/icons-material/MarkEmailReadOutlined';
import { useState } from 'react';


const Subscribe = () => {
    const [email, setEmail] = useState('');

    return (
        <Box width="80%" margin="80px auto" textAlign="center">
            <IconButton>
                <MarkEmailReadOutlinedIcon fontSize='large' />
            </IconButton>
            <Typography variant='h3'>Subscribe To Keep Updated</Typography>
            <Typography variant='h3'>With New Releases and Deals!</Typography>

            {/* Email input box */}
            <Box p="2px 4px" m="15px auto" display="flex"
                alignItems="center" width="75%" backgroundColor="#F2F2F2"
            >
                <InputBase sx={{ ml: 1, flex: 1, borderRadius: "5px" }}
                    placeholder='Enter Email Here'
                    onChange={(e) => setEmail(e.target.value)}
                    value={email}
                />
                <Divider sx={{ height: 28, m: 0.5 }} orientation='vertical' />
                <Typography sx={{ p: "10px", ":hover": { cursor: "pointer" } }}>Subscribe</Typography>
            </Box>
        </Box>
    )
}

export default Subscribe;
